<script setup>
import Splide from "@splidejs/splide";

const instances = [1, 2, 3, 4, 5];
let selected = ref("tours");

const splideOptions = {
    type: "loop",
    drag: "free",
    perPage: 1,
    arrows: true
};

let splide;

onMounted(() => {
    if (process.client) {
        splide = new Splide(".splide", splideOptions);
        splide.mount();
    }
});

watch(selected, (newValue) => {
    if (process.client && splide) {
        splide.destroy();
        splide.options = splideOptions;
        splide.mount();
    }
});
